import React, { useState } from 'react';
import '../../css/AddConsumableForm.css';

const AddConsumableForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        type: '',
        caloriesPer100g: '',
        proteinPer100g: '',
        carbohydratePer100g: '',
        fatPer100g: '',
        nutritionalInfo: {
            carbohydrates: {
                fibers: '',
                starch: '',
                sugars: '',
                //... other fields
            },
            vitamins: {
                betaine: '',
                vitaminA: '',
                //... other fields
            },
            //... amino acids, fats, minerals, more info
        }
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleNutritionalChange = (section, field, value) => {
        setFormData(prevState => ({
            ...prevState,
            nutritionalInfo: {
                ...prevState.nutritionalInfo,
                [section]: {
                    ...prevState.nutritionalInfo[section],
                    [field]: value
                }
            }
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit form data to API
        console.log(formData);
    };

    return (
        <div className="add-consumable-form">
            <h2>Add New Consumable</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Type:</label>
                    <select name="type" value={formData.type} onChange={handleChange} required>
                        <option value="">Select Type</option>
                        <option value="Food">Food</option>
                        <option value="Drink">Drink</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Calories per 100g:</label>
                    <input type="number" name="caloriesPer100g" value={formData.caloriesPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Protein per 100g:</label>
                    <input type="number" step="0.01" name="proteinPer100g" value={formData.proteinPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Carbohydrate per 100g:</label>
                    <input type="number" step="0.01" name="carbohydratePer100g" value={formData.carbohydratePer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Fat per 100g:</label>
                    <input type="number" step="0.01" name="fatPer100g" value={formData.fatPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Carbohydrate Info:</label>
                    <input type="number" step="0.01" name="fibers" value={formData.nutritionalInfo.carbohydrates.fibers} 
                           onChange={(e) => handleNutritionalChange('carbohydrates', 'fibers', e.target.value)} />
                    {/* Repeat for all Carbohydrate fields */}
                </div>

                <div className="form-group">
                    <label>Vitamin Info:</label>
                    <input type="number" step="0.01" name="betaine" value={formData.nutritionalInfo.vitamins.betaine} 
                           onChange={(e) => handleNutritionalChange('vitamins', 'betaine', e.target.value)} />
                    {/* Repeat for all Vitamin fields */}
                </div>

                {/* Repeat similar blocks for Amino Acid Info, Fat Info, Mineral Info, More Info */}

                <button type="submit">Add Consumable</button>
            </form>
        </div>
    );
};

export default AddConsumableForm;
