import React, { useState , useEffect} from 'react';
import InfoCard from '../InfoCard.js';
import '../../css/DashboardPage.css';

const DashboardPage = () => {
    const [goals, setGoals] = useState({
        dailyCalories: '',
        monthlyCalories: '',
        currentWeight: '',
        goalWeight: '',
        height: '',
        isDailyCaloriesGoal: true,
      });
    const [calories, setCalories] = useState(0);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchWeight();
        fetchCalories();
    }, []);

    const fetchWeight = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/user/goals`);
            setWeight(data.weight);
            setGoalWeight(data.goalWeight);
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchCalories = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/meal/calories?date=${formatDate(new Date())}`);
            setCalories(data);
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchData = async (url) => {
        const response = await fetch(url, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        });

        if (!response.ok) {
            throw new Error('Fetching data failed');
        }

        return response.json();
    };
  
    return (
      <div className="dashboard-page">
        <h1>Your Fitness Dashboard</h1>
        <div className="dashboard-grid">
          <InfoCard 
            title="Calories Consumed Today" 
            current={calories} 
            goal={2000} 
            unit="kcal" 
          />
          <InfoCard 
            title="Current Weight" 
            current={goals.weight} 
            goal={goals.goalWeight} 
            unit="kg" 
          />
        </div>
      </div>
    );
  };
  
  export default DashboardPage;
