import React, { useState } from "react";
import { Alert } from "react-bootstrap";
import CustomForm from "../CustomForm.js";

const AddActivityTypeForm = () => {
  const [activityType, setActivityType] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");
    setSuccessMessage("");
    try {
      const response = await fetch(
        "https://localhost:7009/api/admin/add-activity-type",
        {
          method: "POST",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: activityType }),
        }
      );
      const data = await response.json();

      if (response.ok) {
        setSuccessMessage(data.message);
      } else {
        setErrorMessage(data.message);
      }
    } catch (err) {
      console.log(err);
      setErrorMessage(err.message);
      setSuccessMessage("");
    } finally {
      setIsLoading(false);
      resetForm();
    }
  };

  const handleChange = (e) => {
    setActivityType(e.target.value);
  };

  const resetForm = () => {
    setActivityType("");
  };

  const formOptions = [
    {
      title: "Activity Type Name",
      type: "text",
      value: activityType,
      handleChange: handleChange,
      handleSubmit: handleSubmit,
      isRequired: true,
      placeholder: "Enter activity type name",
    },
  ];

  return (
    <>
      <CustomForm
        handleSubmit={handleSubmit}
        formOptions={formOptions}
        isLoading={isLoading}
        buttonText="Add activity type"
        successMessage={successMessage}
        errorMessage={errorMessage}
      />
    </>
  );
};

export default AddActivityTypeForm;
