import React, { createContext, useState, useEffect } from 'react';

export const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await fetch('https://localhost:7009/api/user/authstatus', {
        method: 'GET',
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('User is not authenticated');
      }

      const user = await response.json();
      console.log(user.roles);
      setIsAuthenticated(true);
      setUserInfo(user);

      if (user.Roles && user.Roles.includes('Administrator')) {
        setIsAdmin(true);
      } else {
        setIsAdmin(false);
      }

    } catch (err) {
      setIsAuthenticated(false);
      setUserInfo(null);
    }
  };

  const login = async (email, password) => {
    try {
      const response = await fetch('https://localhost:7009/api/user/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Email: email, Password: password }),
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Login failed');
      }

      await checkAuthStatus();
    } catch (err) {
      setIsAuthenticated(false);
      setUserInfo(null);
    }
  };

  const logout = async () => {
    try {
      await fetch('https://localhost:7009/api/user/logout', {
        method: 'POST',
        credentials: 'include',
      });

      setIsAuthenticated(false);
      setUserInfo(null);
    } catch (err) {
      console.error('Logout failed:', err.message);
    }
  };

  const authContextValue = {
    isAuthenticated,
    userInfo,
    isAdmin,
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider;
