const DashboardPage = () => {
    const [userDataAndGoals, setUserDataAndGoals] = useState({
        dailyCalories: 0,
        monthlyCalories: 0,
        currentWeight: 0,
        goalWeight: 0,
        height: 0,
        isDailyCaloriesGoal: true,
    });
    const [calories, setCalories] = useState(0);
    const [errorMessage, setErrorMessage] = useState('');
    const [isFormVisible, setIsFormVisible] = useState(false);

    useEffect(() => {
        fetchUserDataAndGoals();
        fetchCalories();
    }, []);

    const fetchUserDataAndGoals = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/user/goals`);
            setUserDataAndGoals({
                dailyCalories: data.dailyCaloriesGoal || 0,
                monthlyCalories: data.monthlyCaloriesGoal || 0,
                currentWeight: data.weight || 0,
                goalWeight: data.goalWeight || 0,
                height: data.height || 0,
                isDailyCaloriesGoal: data.isDailyCaloriesGoal,
            });
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchCalories = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/meal/calories?date=${formatDate(new Date())}`);
            setCalories(data);
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchData = async (url) => {
        const response = await fetch(url, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        });

        if (!response.ok) {
            throw new Error('Fetching data failed');
        }

        return response.json();
    };

    const formatDate = (date) => {
        return date.toISOString().split('T')[0];
    };

    const handleAddButtonClick = () => {
        setIsFormVisible(true);
    };

    const handleFormClose = () => {
        setIsFormVisible(false);
    };

    const {
        dailyCalories,
        monthlyCalories,
        currentWeight,
        goalWeight,
        height,
    } = userDataAndGoals;

    return (
        <div className={`dashboard-page ${isFormVisible ? 'overlay' : ''}`}>
            <button className="add-button" onClick={handleAddButtonClick}>Add</button>
            {isFormVisible && (
                <div className="form-modal" onClick={handleFormClose}>
                    <AddMealForm />
                </div>
            )}
            <div className="upper-section">
                <AllMealsPage />
            </div>
            <div className="main-content">
                <div className="left-section">
                    <InfoCard
                        title="Calories Consumed Today"
                        current={calories}
                        goal={dailyCalories}
                        unit="kcal"
                    />
                    {/* Add more InfoCards here for scalability */}
                </div>
                <div className="right-section">
                    <InfoCard
                        title="Current Weight"
                        current={currentWeight}
                        goal={goalWeight}
                        unit="kg"
                    />
                    {/* Add more InfoCards here for scalability */}
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
