import React, { useState } from "react";
import CustomForm from "../CustomForm.js";

const AddActivityTypeForm = () => {
  const [activityType, setActivityType] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");
    setSuccessMessage("");
    // try {
    //   const response = await fetch(
    //     "https://localhost:7009/api/admin/add-activity-type",
    //     {
    //       method: "POST",
    //       credentials: "include",
    //       headers: {
    //         "Content-Type": "application/json",
    //       },
    //       body: JSON.stringify(activityType),
    //     }
    //   );

    //   if (!response.ok) {
    //     throw new Error("Adding an activity type failed!");
    //   }
    try {
      console.log(`Activity type is set to ${activityType}`);
      setSuccessMessage("Activity type has been added successfully!");
      resetForm();
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    setActivityType(e.target.value);
  };

  const resetForm = () => {
    setActivityType("");
  };

  const formOptions = [
    {
      title: "Activity Type Name",
      type: "text",
      value: activityType,
      handleChange: handleChange,
      handleSubmit: handleSubmit,
      isRequired: true,
      placeholder: "Enter activity type name",
    },
  ];

  return (
    <>
      <CustomForm formOptions={formOptions} />
    </>
  );
};

export default AddActivityTypeForm;
