import React from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, MeshDistortMaterial, Sphere } from '@react-three/drei';
import { motion } from 'framer-motion';
import styled from 'styled-components';

const Home = () => {
  return (
    <Container>
      <CanvasContainer>
        <Canvas>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <Sphere args={[1, 100, 200]} scale={2}>
            <MeshDistortMaterial color="#8352fd" distort={0.5} speed={2} />
          </Sphere>
          <OrbitControls enableZoom={false} />
        </Canvas>
      </CanvasContainer>

      <TextContainer>
        <AnimatedHeading
          initial={{ y: -250 }}
          animate={{ y: -10 }}
          transition={{ delay: 0.2, type: 'spring', stiffness: 120 }}
        >
          Welcome to FitLife
        </AnimatedHeading>
        <AnimatedParagraph
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4, duration: 1.5 }}
        >
          Set your fitness goals, track your progress, and transform your life with our all-in-one fitness app.
        </AnimatedParagraph>
        <AnimatedButton
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.9 }}
        >
          Get Started
        </AnimatedButton>
      </TextContainer>
    </Container>
  );
};

export default Home;

// Styled Components
const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  overflow: hidden;
`;

const CanvasContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const TextContainer = styled.div`
  flex: 1;
  color: white;
  z-index: 1;
  padding: 20px;
`;

// Combining framer-motion and styled-components
const AnimatedHeading = motion(styled.h1`
  font-size: 4rem;
  margin-bottom: 20px;
`);

const AnimatedParagraph = motion(styled.p`
  font-size: 1.5rem;
  margin-bottom: 30px;
  line-height: 1.5;
`);

const AnimatedButton = motion(styled.button`
  font-size: 1.2rem;
  padding: 10px 20px;
  color: white;
  background-color: #ff4d4d;
  border: none;
  border-radius: 25px;
  cursor: pointer;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
  transition: all 0.3s ease;
`);
