import React, { useState } from "react";
import { Container, Form, Alert, Spinner, Button } from "react-bootstrap";

const CustomForm = ({
  handleSubmit,
  formOptions,
  isLoading,
  buttonText,
  successMessage,
  errorMessage,
}) => {
  return (
    <Card>
      <CardHeader>
        <CardTitle>{title}</CardTitle>
        <CardDescription>{description}</CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={onSubmit}>
          <div className="grid w-full items-center gap-4">
            <div className="flex flex-col space-y-1.5">
              <Label htmlFor="name">Name</Label>
              <Input
                id="name"
                placeholder="Enter name"
                value={value}
                onChange={(e) => onChange(e.target.value)}
              />
            </div>
            <Button type="submit" disabled={isLoading}>
              {isLoading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Please wait
                </>
              ) : (
                "Submit"
              )}
            </Button>
          </div>
        </form>
        {successMessage && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="mt-4 p-3 bg-green-100 text-green-700 rounded-md flex items-center"
          >
            <CheckCircle className="mr-2" />
            {successMessage}
          </motion.div>
        )}
        {errorMessage && (
          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            className="mt-4 p-3 bg-red-100 text-red-700 rounded-md flex items-center"
          >
            <AlertCircle className="mr-2" />
            {errorMessage}
          </motion.div>
        )}
      </CardContent>
    </Card>
  );
};

export default CustomForm;
