import React, { useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Link, NavLink } from 'react-router-dom';
import Home from './Pages/Home';
import MealsNavigation from './MealsNavigation.js';
import AddMealForm from './Forms/AddMealForm.js';
import AllMealsPage from './Pages/AllMealsPage';
import Register from './Pages/RegisterPage';
import Login from './Pages/LoginPage';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { AuthContext } from '../Contexts/AuthContext';
import ProtectedRoute from "./ProtectedRoute.js";

const Navigation = () => {
  const { isAuthenticated, userInfo, logout } = useContext(AuthContext);

  return (
    <Router>
      <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
        <Container>
          <Navbar.Brand as={Link} to="/">
            MyApp
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">
              <NavLink to="/" className="nav-link">
                Home
              </NavLink>
              {isAuthenticated && (
                <NavLink to="/meals" className="nav-link">
                  Meals
                </NavLink>
              )}
              {!isAuthenticated ? (
                <>
                  <NavLink to="/register" className="nav-link">
                    Register
                  </NavLink>
                  <NavLink to="/login" className="nav-link">
                    Login
                  </NavLink>
                </>
              ) : (
                <>
                  <span className="nav-link">Welcome, {userInfo?.userName}</span>
                  <button onClick={logout} className="nav-link">Logout</button>
                </>
              )}
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <Container>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/meals/*" element={
            <ProtectedRoute>
              <MealsNavigation />
            </ProtectedRoute>}>
            <Route index element={<AllMealsPage />} />
            <Route path="all-meals" element={<AllMealsPage />} />
            <Route path="add-meal" element={<AddMealForm />} />
          </Route>
          <Route path="/register" element={<Register />} />
          <Route path="/login" element={<Login />} />
        </Routes>
      </Container>
    </Router>
  );
};

export default Navigation;
