import React, { useState } from "react";
import { Container, Form, Alert, Spinner, Button } from "react-bootstrap";

const CustomForm = ({
  title,
  api,
  handleSubmit,
  handleChange,
  numberOfFields,
  formOptions,
}) => {
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const [isLoading, setIsLoading] = useState(false);
  return (
    <Container className="mt-5 mb-5">
      <h2 className="mb-4">{title}</h2>
      {errorMessage && (
        <Alert variant="danger" className="mt-3">
          {errorMessage}
        </Alert>
      )}
      {successMessage && (
        <Alert variant="success" className="mt-3">
          {successMessage}
        </Alert>
      )}
      <Form onSubmit={handleSubmit}>
        {Array.from({ length: formOptions.formsCount }).map((_, index) => (
          <Form.Group controlId={formOptions[index].title} className="mb-3">
            <Form.Label>{formOptions[index].title}</Form.Label>
            <Form.Control
              type={formOptions[index].type}
              name={formOptions[index].title}
              value={activityCategory}
              onChange={handleChange}
              required={formOptions[index].isRequired}
              placeholder={formOptions[index].placeholder}
            />
          </Form.Group>
        ))}
        <Button variant="primary" type="submit" disabled={isLoading}>
          {isLoading ? (
            <>
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
                className="me-2"
              />
              Loading...
            </>
          ) : (
            "Add Activity Category"
          )}
        </Button>
      </Form>
    </Container>
  );
};
