import React from 'react';
import InfoCard from '../InfoCard.js';
import '../../css/DashboardPage.css';

const DashboardPage = () => {
  // Hardcoded data for now
  const consumedCalories = 1500;
  const dailyCaloriesGoal = 2000;
  const currentWeight = 75; // in kg
  const goalWeight = 70; // in kg

  return (
    <div className="dashboard-page">
      <h1>Your Fitness Dashboard</h1>
      <div className="dashboard-grid">
        <InfoCard 
          title="Calories Consumed Today" 
          value={consumedCalories} 
          goal={dailyCaloriesGoal} 
          unit="kcal" 
        />
        <InfoCard 
          title="Current Weight" 
          value={currentWeight} 
          goal={goalWeight} 
          unit="kg" 
        />
        <InfoCard 
          title="Weight to Lose" 
          value={currentWeight - goalWeight} 
          unit="kg" 
        />
        {/* You can easily add more InfoCards here */}
      </div>
    </div>
  );
};

export default DashboardPage;
