import React, { useState } from 'react';
import '../../css/GeneralForm.css';
import { Form, Button, Container } from 'react-bootstrap';

const GeneralForm = async () => {
  const [email, setEmail] = useState("yeah, buddy");

  // try {
  //   const response = await fetch('https://localhost:7009/api/user/profile', {
  //     method: 'POST',
  //     credentials: 'include'
  //   });

  //   if (!response.ok) {
  //     throw new Error('Retrieving email failed');
  //   }
  //   const data = await response.json();
  //   setEmail(data)
  // } catch (err) {
  // }

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit the email change
  };

  return (
    <Container className="general-form">
      <h2>General Information</h2>
      <Form onSubmit={handleSubmit} className="general-form">
        <FormGroup controlId="email">
          <FormLabel>Email Address</FormLabel>
          <FormControl
            type="email"
            onChange={(e) => setEmail(e.target.value)}
            placeholder="test"
          />
        </FormGroup>
        <Button variant="primary" type="submit" className="fancy-button">
          Update Email
        </Button>
      </Form>
    </Container>
  );
};

export default GeneralForm;
