import React from 'react';
import InfoCard from '../InfoCard.js';
import '../../css/DashboardPage.css';

const DashboardPage = () => {
    const [calories, setCalories] = useState(0);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        fetchMeals();
        fetchCalories();
    }, [selectedDate]);

    const fetchCalories = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/meal/calories?date=${formatDate(selectedDate)}`);
            setCalories(data);
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchData = async (url) => {
        const response = await fetch(url, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        });

        if (!response.ok) {
            throw new Error('Fetching data failed');
        }

        return response.json();
    };

    const currentWeight = 75; // in kg
    const goalWeight = 70; // in kg
  
    return (
      <div className="dashboard-page">
        <h1>Your Fitness Dashboard</h1>
        <div className="dashboard-grid">
          <InfoCard 
            title="Calories Consumed Today" 
            current={1500} 
            goal={2000} 
            unit="kcal" 
          />
          <InfoCard 
            title="Current Weight" 
            current={currentWeight} 
            goal={goalWeight} 
            unit="kg" 
          />
        </div>
      </div>
    );
  };
  
  export default DashboardPage;
