import React, { useState } from 'react';

const AddConsumableForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        type: '',
        caloriesPer100g: '',
        proteinPer100g: '',
        carbohydratePer100g: '',
        fatPer100g: '',
        nutritionalInfo: {
            carbohydrates: {
                fibers: '',
                starch: '',
                sugars: '',
                galactose: '',
                glucose: '',
                sucrose: '',
                lactose: '',
                maltose: '',
                fructose: '',
            },
            vitamins: {
                betaine: '',
                vitaminA: '',
                vitaminB1: '',
                vitaminB2: '',
                vitaminB3: '',
                vitaminB4: '',
                vitaminB5: '',
                vitaminB6: '',
                vitaminB9: '',
                vitaminB12: '',
                vitaminC: '',
                vitaminD: '',
                vitaminE: '',
                vitaminK1: '',
                vitaminK2: '',
            },
            aminoAcids: {
                alanine: '',
                arginine: '',
                asparticAcid: '',
                valine: '',
                glycine: '',
                glutamine: '',
                isoleucine: '',
                leucine: '',
                lysine: '',
                methionine: '',
                proline: '',
                serine: '',
                tyrosine: '',
                threonine: '',
                tryptophan: '',
                phenylalanine: '',
                hydroxyproline: '',
                histidine: '',
                cystine: '',
            },
            fats: {
                totalFats: '',
                monounsaturatedFats: '',
                polyunsaturatedFats: '',
                saturatedFats: '',
                transFats: '',
            },
            minerals: {
                iron: '',
                potassium: '',
                calcium: '',
                magnesium: '',
                manganese: '',
                copper: '',
                sodium: '',
                selenium: '',
                fluoride: '',
                phosphorus: '',
                zinc: '',
                sterols: '',
                cholesterol: '',
                phytosterols: '',
                stigmasterol: '',
                campesterol: '',
                betaSitosterol: '',
            },
            moreInfo: {
                alcohol: '',
                water: '',
                caffeine: '',
                theobromine: '',
                ashes: '',
            }
        }
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleNutritionalChange = (section, field, value) => {
        setFormData(prevState => ({
            ...prevState,
            nutritionalInfo: {
                ...prevState.nutritionalInfo,
                [section]: {
                    ...prevState.nutritionalInfo[section],
                    [field]: value
                }
            }
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Submit form data to API
        console.log(formData);
    };

    return (
        <div className="add-consumable-form">
            <h2>Add New Consumable</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Type:</label>
                    <select name="type" value={formData.type} onChange={handleChange} required>
                        <option value="">Select Type</option>
                        <option value="Food">Food</option>
                        <option value="Drink">Drink</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Calories per 100g:</label>
                    <input type="number" name="caloriesPer100g" value={formData.caloriesPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Protein per 100g:</label>
                    <input type="number" step="0.01" name="proteinPer100g" value={formData.proteinPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Carbohydrate per 100g:</label>
                    <input type="number" step="0.01" name="carbohydratePer100g" value={formData.carbohydratePer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Fat per 100g:</label>
                    <input type="number" step="0.01" name="fatPer100g" value={formData.fatPer100g} onChange={handleChange} required />
                </div>

                <h3>Carbohydrate Information</h3>
                {Object.keys(formData.nutritionalInfo.carbohydrates).map(key => (
                    <div className="form-group" key={key}>
                        <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                        <input
                            type="number"
                            step="0.01"
                            name={key}
                            value={formData.nutritionalInfo.carbohydrates[key]}
                            onChange={(e) => handleNutritionalChange('carbohydrates', key, e.target.value)}
                        />
                    </div>
                ))}

                <h3>Vitamin Information</h3>
                {Object.keys(formData.nutritionalInfo.vitamins).map(key => (
                    <div className="form-group" key={key}>
                        <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                        <input
                            type="number"
                            step="0.01"
                            name={key}
                            value={formData.nutritionalInfo.vitamins[key]}
                            onChange={(e) => handleNutritionalChange('vitamins', key, e.target.value)}
                        />
                    </div>
                ))}

                <h3>Amino Acid Information</h3>
                {Object.keys(formData.nutritionalInfo.aminoAcids).map(key => (
                    <div className="form-group" key={key}>
                        <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                        <input
                            type="number"
                            step="0.01"
                            name={key}
                            value={formData.nutritionalInfo.aminoAcids[key]}
                            onChange={(e) => handleNutritionalChange('aminoAcids', key, e.target.value)}
                        />
                    </div>
                ))}

                <h3>Fat Information</h3>
                {Object.keys(formData.nutritionalInfo.fats).map(key => (
                    <div className="form-group" key={key}>
                        <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                        <input
                            type="number"
                            step="0.01"
                            name={key}
                            value={formData.nutritionalInfo.fats[key]}
                            onChange={(e) => handleNutritionalChange('fats', key, e.target.value)}
                        />
                    </div>
                ))}

                <h3>Mineral Information</h3>
                {Object.keys(formData.nutritionalInfo.minerals).map(key => (
                    <div className="form-group" key={key}>
                        <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                        <input
                            type="number"
                            step="0.01"
                            name={key}
                            value={formData.nutritionalInfo.minerals[key]}
                            onChange={(e) => handleNutritionalChange('minerals', key, e.target.value)}
                        />
                    </div>
                ))}

                <h3>More Information</h3>
                {Object.keys(formData.nutritionalInfo.moreInfo).map(key => (
                    <div className="form-group" key={key}>
                        <label>{key.charAt(0).toUpperCase() + key.slice(1)}:</label>
                        <input
                            type="number"
                            step="0.01"
                            name={key}
                            value={formData.nutritionalInfo.moreInfo[key]}
                            onChange={(e) => handleNutritionalChange('moreInfo', key, e.target.value)}
                        />
                    </div>
                ))}

                <button type="submit">Add Consumable</button>
            </form>
        </div>
    );
};

export default AddConsumableForm;
