import React, { useState } from "react";
import { Alert } from "react-bootstrap";
import CustomForm from "../CustomForm.js";

const AddActivityCategoryForm = () => {
  const [activityCategory, setActivityCategory] = useState("");

  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");
    setSuccessMessage("");
    // try {
    //   const response = await fetch(
    //     "https://localhost:7009/api/admin/add-activity-category",
    //     {
    //       method: "POST",
    //       credentials: "include",
    //       headers: {
    //         "Content-Type": "application/json",
    //       },
    //       body: JSON.stringify(activityCategory),
    //     }
    //   );

    //   if (!response.ok) {
    //     throw new Error("Adding an activity category failed!");
    //   }
    try {
      console.log(`Activity category is set to ${activityCategory}`);
      setSuccessMessage("Activity category has been added successfully!");
      resetForm();
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    setActivityCategory(e.target.value);
  };

  const resetForm = () => {
    setActivityCategory("");
  };

  const formOptions = [
    {
      title: "Activity Category Name",
      type: "text",
      value: activityCategory,
      handleChange: handleChange,
      handleSubmit: handleSubmit,
      isRequired: true,
      placeholder: "Enter activity group name",
    },
  ];

  return (
    <>
      {errorMessage && (
        <Alert variant="danger" className="mt-3">
          {errorMessage}
        </Alert>
      )}
      {successMessage && (
        <Alert variant="success" className="mt-3">
          {successMessage}
        </Alert>
      )}
      <CustomForm
        handleSubmit={handleSubmit}
        formOptions={formOptions}
        isLoading={isLoading}
        buttonText="Add activity category"
      />
    </>
  );
};

export default AddActivityCategoryForm;
