import React, { useState, useEffect, useCallback } from "react";
import { Container, Form, Alert, Spinner, Button } from "react-bootstrap";
import { motion } from "framer-motion";
import styled from "styled-components";
import { FaPlus, FaRunning } from "react-icons/fa";
import "../../css/CustomForm.css";

const StyledContainer = styled(motion.div)`
  background: linear-gradient(135deg, #6e8efb, #a777e3);
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
  padding: 2rem;
  color: white;
`;

const StyledForm = styled(Form)`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const StyledInput = styled(Form.Control)`
  border: none;
  border-bottom: 2px solid rgba(255, 255, 255, 0.5);
  background: transparent;
  color: white;
  transition: all 0.3s ease;

  &:focus {
    box-shadow: none;
    border-bottom-color: white;
  }

  &::placeholder {
    color: rgba(255, 255, 255, 0.7);
  }
`;

const StyledSelect = styled(Form.Select)`
  background: transparent;
  color: white;
  border: 2px solid rgba(255, 255, 255, 0.5);
  transition: all 0.3s ease;

  &:focus {
    box-shadow: none;
    border-color: white;
  }

  option {
    color: black;
  }
`;

const StyledButton = styled(motion.button)`
  background: white;
  color: #6e8efb;
  border: none;
  padding: 0.75rem 1.5rem;
  font-weight: bold;
  border-radius: 30px;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  transition: all 0.3s ease;

  &:hover {
    background: #f0f0f0;
    transform: translateY(-2px);
  }
`;

const AddActivityTypeForm = () => {
  const [formData, setFormData] = useState({
    activityType: "",
    selectedActivityCategoryId: "",
  });
  const [status, setStatus] = useState({
    error: "",
    success: "",
    isLoading: false,
  });
  const [activityOptions, setActivityOptions] = useState([]);

  const fetchOptions = useCallback(async () => {
    try {
      const response = await fetch(
        "https://localhost:7009/api/admin/get-activity-categories",
        { method: "GET", credentials: "include" }
      );
      if (!response.ok) throw new Error("Failed to fetch activity categories");
      const data = await response.json();
      setActivityOptions(data);
    } catch (err) {
      console.error("Couldn't retrieve options:", err);
      setStatus((prev) => ({
        ...prev,
        error: "Failed to load activity categories",
      }));
    }
  }, []);

  useEffect(() => {
    fetchOptions();
  }, [fetchOptions]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus({ error: "", success: "", isLoading: true });
    try {
      const response = await fetch(
        "https://localhost:7009/api/admin/add-activity-type",
        {
          method: "POST",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            Name: formData.activityType,
            ActivityCategoryId: formData.selectedActivityCategoryId,
          }),
        }
      );
      const data = await response.json();
      if (!response.ok)
        throw new Error(data.message || "Failed to add activity type");
      setStatus((prev) => ({ ...prev, success: data.message }));
      setFormData((prev) => ({ ...prev, activityType: "" }));
    } catch (err) {
      console.error(err);
      setStatus((prev) => ({ ...prev, error: err.message }));
    } finally {
      setStatus((prev) => ({ ...prev, isLoading: false }));
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prev) => ({
      ...prev,
      [name === "Activity Type Name"
        ? "activityType"
        : "selectedActivityCategoryId"]: value,
    }));
  };

  return (
    <StyledContainer
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="mt-5 mb-5"
    >
      <motion.h2
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.2 }}
        className="mb-4"
      >
        Add New Activity Type <FaRunning />
      </motion.h2>
      {status.error && (
        <motion.div
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.3 }}
        >
          <Alert variant="danger">{status.error}</Alert>
        </motion.div>
      )}
      {status.success && (
        <motion.div
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.3 }}
        >
          <Alert variant="success">{status.success}</Alert>
        </motion.div>
      )}
      <StyledForm onSubmit={handleSubmit}>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
        >
          <Form.Group>
            <Form.Label>Activity Type Name</Form.Label>
            <StyledInput
              type="text"
              name="Activity Type Name"
              value={formData.activityType}
              onChange={handleChange}
              required
              placeholder="Enter activity type name"
            />
          </Form.Group>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.4 }}
        >
          <Form.Group>
            <Form.Label>Select an Option</Form.Label>
            <StyledSelect
              name="Activity Category"
              value={formData.selectedActivityCategoryId}
              onChange={handleChange}
              required
            >
              <option value="" disabled>
                Choose an option...
              </option>
              {activityOptions.map((option) => (
                <option key={option.id} value={option.id}>
                  {option.name}
                </option>
              ))}
            </StyledSelect>
          </Form.Group>
        </motion.div>
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
        >
          <StyledButton
            type="submit"
            disabled={status.isLoading}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {status.isLoading ? (
              <>
                <Spinner
                  as="span"
                  animation="border"
                  size="sm"
                  role="status"
                  aria-hidden="true"
                />
                Loading...
              </>
            ) : (
              <>
                <FaPlus /> Add Activity Type
              </>
            )}
          </StyledButton>
        </motion.div>
      </StyledForm>
    </StyledContainer>
  );
};

export default AddActivityTypeForm;
