import React, { useState } from "react";
import { Container, Form, Alert, Spinner } from "react-bootstrap";
import "../../css/AddActivityCategoryForm.css";

const AddActivityCategoryForm = () => {
  const [activityCategory, setActivityCategory] = useState("");

  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");
    setSuccessMessage("");

    try {
      const response = await fetch(
        "https://localhost:7009/api/admin/add-activity-category",
        {
          method: "POST",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(activityCategory),
        }
      );

      if (!response.ok) {
        throw new Error("Adding an activity category failed!");
      }

      setSuccessMessage("Activity category has been added successfully!");
      resetForm();
    } catch (err) {
      setErrorMessage(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e) => {
    setActivityCategory(e.target.value);
  };

  const resetForm = () => {
    setActivityCategory("");
  };

  return (
    <Container className="add-activity-category-form mt-5 mb-5">
      <h2 className="mb-4">Add New Activity Category</h2>
      {errorMessage && (
        <Alert variant="danger" className="mt-3">
          {errorMessage}
        </Alert>
      )}
      {successMessage && (
        <Alert variant="success" className="mt-3">
          {successMessage}
        </Alert>
      )}
      <Form onSubmit={handleSubmit}>
        {/* Name */}
        <Form.Group controlId="name" className="mb-3">
          <Form.Label>Name:</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={activityCategory}
            onChange={handleChange}
            required
            placeholder="Enter activity group name"
          />
        </Form.Group>

        {/* Submit Button */}
        <Button variant="primary" type="submit" disabled={isLoading}>
          {isLoading ? (
            <>
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
                className="me-2"
              />
              Loading...
            </>
          ) : (
            "Add Activity Category"
          )}
        </Button>
      </Form>
    </Container>
  );
};

export default AddActivityCategoryForm;
