import React, { useState } from 'react';
import '../../css/AddConsumableForm.css';

const AddConsumableForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        type: '',
        caloriesPer100g: '',
        proteinPer100g: '',
        carbohydratePer100g: '',
        fatPer100g: '',
        nutritionalInfo: {
            carbohydrates: {
                fibers: '',
                starch: '',
                sugars: '',
                galactose: '',
                glucose: '',
                sucrose: '',
                lactose: '',
                maltose: '',
                fructose: '',
            },
            vitamins: {
                betaine: '',
                vitaminA: '',
                vitaminB1: '',
                vitaminB2: '',
                vitaminB3: '',
                vitaminB4: '',
                vitaminB5: '',
                vitaminB6: '',
                vitaminB9: '',
                vitaminB12: '',
                vitaminC: '',
                vitaminD: '',
                vitaminE: '',
                vitaminK1: '',
                vitaminK2: '',
            },
            aminoAcids: {
                alanine: '',
                arginine: '',
                asparticAcid: '',
                valine: '',
                glycine: '',
                glutamine: '',
                isoleucine: '',
                leucine: '',
                lysine: '',
                methionine: '',
                proline: '',
                serine: '',
                tyrosine: '',
                threonine: '',
                tryptophan: '',
                phenylalanine: '',
                hydroxyproline: '',
                histidine: '',
                cystine: '',
            },
            fats: {
                totalFats: '',
                monounsaturatedFats: '',
                polyunsaturatedFats: '',
                saturatedFats: '',
                transFats: '',
            },
            minerals: {
                iron: '',
                potassium: '',
                calcium: '',
                magnesium: '',
                manganese: '',
                copper: '',
                sodium: '',
                selenium: '',
                fluoride: '',
                phosphorus: '',
                zinc: '',
                sterols: '',
                cholesterol: '',
                phytosterols: '',
                stigmasterol: '',
                campesterol: '',
                betaSitosterol: '',
            },
            moreInfo: {
                alcohol: '',
                water: '',
                caffeine: '',
                theobromine: '',
                ashes: '',
            }
        }
    });

    const [sections, setSections] = useState({
        carbohydrates: false,
        vitamins: false,
        aminoAcids: false,
        fats: false,
        minerals: false,
        moreInfo: false,
    });
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const toggleSection = (section) => {
        setSections(prevState => ({
            ...prevState,
            [section]: !prevState[section]
        }));
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleNutritionalChange = (section, key, value) => {
        setFormData(prevState => ({
            ...prevState,
            nutritionalInfo: {
                ...prevState.nutritionalInfo,
                [section]: {
                    ...prevState.nutritionalInfo[section],
                    [key]: value
                }
            }
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // THIS IS JUST A TEST OBJECT THAT IS PRE-MADE
        const consumableItemDto = {
            name: 'Apple',
            caloriesPer100g: 52,
            proteinPer100g: 0.3,
            carbohydratePer100g: 14,
            fatPer100g: 0.2,
            type: 0,  // Assuming 0 corresponds to a specific TypeOfConsumable enum value
            nutritionalInformation: {
                carbohydrates: {
                    fibers: 2.4,
                    starch: 0,
                    sugars: 10.4,
                    galactose: 0,
                    glucose: 2.4,
                    sucrose: 6.0,
                    lactose: 0,
                    maltose: 0,
                    fructose: 6.0
                },
                vitamins: {
                    betaine: 0,
                    vitaminA: 54,
                    vitaminB1: 0.017,
                    vitaminB2: 0.026,
                    vitaminB3: 0.091,
                    vitaminB4: 0.7,
                    vitaminB5: 0.061,
                    vitaminB6: 0.041,
                    vitaminB9: 3,
                    vitaminB12: 0,
                    vitaminC: 4.6,
                    vitaminD: 0,
                    vitaminE: 0.18,
                    vitaminK1: 2.2,
                    vitaminK2: 0
                },
                aminoAcids: {
                    alanine: 0,
                    arginine: 0,
                    asparticAcid: 0,
                    valine: 0,
                    glycine: 0,
                    glutamine: 0,
                    isoleucine: 0,
                    leucine: 0,
                    lysine: 0,
                    methionine: 0,
                    proline: 0,
                    serine: 0,
                    tyrosine: 0,
                    threonine: 0,
                    tryptophan: 0,
                    phenylalanine: 0,
                    hydroxyproline: 0,
                    histidine: 0,
                    cystine: 0
                },
                fats: {
                    totalFats: 0.2,
                    monounsaturatedFats: 0.007,
                    polyunsaturatedFats: 0.051,
                    saturatedFats: 0.033,
                    transFats: 0
                },
                minerals: {
                    iron: 0.12,
                    potassium: 107,
                    calcium: 6,
                    magnesium: 5,
                    manganese: 0.035,
                    copper: 0.027,
                    sodium: 1,
                    selenium: 0,
                    fluoride: 3.3,
                    phosphorus: 11,
                    zinc: 0.04,
                    sterols: 0,
                    cholesterol: 0,
                    phytosterols: 0,
                    stigmasterol: 0,
                    campesterol: 0,
                    betaSitosterol: 0
                },
                more: {
                    alcohol: 0,
                    water: 85.6,
                    caffeine: 0,
                    theobromine: 0,
                    ashes: 0.3
                }
            }
        };

        //const typeOfConsumableItemAsInt = parseInt(mealOfTheDay);

        try {
            const response = await fetch('https://localhost:7009/api/admin/add-consumable-item', {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(consumableItemDto),
            });

            if (!response.ok) {
                throw new Error('Adding a consumable item failed');
            }

            setSuccessMessage('Consumable item added successfully!');
        } catch (err) {
            setErrorMessage(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const renderNutritionalSection = (sectionName, sectionData, title) => (
        <div className={`collapsible-card ${sections[sectionName] ? 'expanded' : ''}`}>
            <div className="collapsible-header" onClick={() => toggleSection(sectionName)}>
                <h3>{title}</h3>
            </div>
            {sections[sectionName] && (
                <div className="collapsible-content grid-container">
                    {Object.entries(sectionData).map(([key, value]) => (
                        <div className="form-group grid-item" key={key}>
                            <label>{key.charAt(0).toUpperCase() + key.slice(1)}</label>
                            <input
                                type="number"
                                step="0.01"
                                name={key}
                                value={value}
                                onChange={(e) => handleNutritionalChange(sectionName, key, e.target.value)}
                            />
                        </div>
                    ))}
                </div>
            )}
        </div>
    );

    return (
        <div className="add-consumable-form">
            <h2>Add New Consumable</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Type:</label>
                    <select name="type" value={formData.type} onChange={handleChange} required>
                        <option value="">Select Type</option>
                        <option value="Food">Food</option>
                        <option value="Drink">Drink</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Calories per 100g:</label>
                    <input type="number" name="caloriesPer100g" value={formData.caloriesPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Protein per 100g:</label>
                    <input type="number" step="0.01" name="proteinPer100g" value={formData.proteinPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Carbohydrate per 100g:</label>
                    <input type="number" step="0.01" name="carbohydratePer100g" value={formData.carbohydratePer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Fat per 100g:</label>
                    <input type="number" step="0.01" name="fatPer100g" value={formData.fatPer100g} onChange={handleChange} required />
                </div>

                {renderNutritionalSection('carbohydrates', formData.nutritionalInfo.carbohydrates, 'Carbohydrate Information')}
                {renderNutritionalSection('vitamins', formData.nutritionalInfo.vitamins, 'Vitamin Information')}
                {renderNutritionalSection('aminoAcids', formData.nutritionalInfo.aminoAcids, 'Amino Acid Information')}
                {renderNutritionalSection('fats', formData.nutritionalInfo.fats, 'Fat Information')}
                {renderNutritionalSection('minerals', formData.nutritionalInfo.minerals, 'Mineral Information')}
                {renderNutritionalSection('moreInfo', formData.nutritionalInfo.moreInfo, 'More Information')}

                <button type="submit" className="submit-button">Add Consumable</button>
            </form>
        </div>
    );
};

export default AddConsumableForm;
