import React, { useState } from 'react';
import '../../css/GoalsForm.css';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';

const GoalsForm = () => {
  const [goals, setGoals] = useState({
    dailyCalories: '',
    monthlyCalories: '',
    currentWeight: '',
    goalWeight: '',
    height: '',
  });

  const handleChange = (e) => {
    setGoals({ ...goals, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit the goals data
  };

  return (
    <Container className="goals-page">
      <h2>Your Goals</h2>
      <Form onSubmit={handleSubmit} className="goals-form">
        <Row>
          <Col md={6}>
            <Form.Group controlId="dailyCalories">
              <Form.Label>Daily Calories Goal</Form.Label>
              <Form.Control
                type="number"
                name="dailyCalories"
                value={goals.dailyCalories}
                onChange={handleChange}
                placeholder="Enter daily calories goal"
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="monthlyCalories">
              <Form.Label>Monthly Calories Goal</Form.Label>
              <Form.Control
                type="number"
                name="monthlyCalories"
                value={goals.monthlyCalories}
                onChange={handleChange}
                placeholder="Enter monthly calories goal"
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col md={6}>
            <Form.Group controlId="currentWeight">
              <Form.Label>Current Weight (kg)</Form.Label>
              <Form.Control
                type="number"
                name="currentWeight"
                value={goals.currentWeight}
                onChange={handleChange}
                placeholder="Enter current weight"
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="goalWeight">
              <Form.Label>Goal Weight (kg)</Form.Label>
              <Form.Control
                type="number"
                name="goalWeight"
                value={goals.goalWeight}
                onChange={handleChange}
                placeholder="Enter goal weight"
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col md={12}>
            <Form.Group controlId="height">
              <Form.Label>Height (cm)</Form.Label>
              <Form.Control
                type="number"
                name="height"
                value={goals.height}
                onChange={handleChange}
                placeholder="Enter height"
              />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" type="submit" className="fancy-button">
          Save Goals
        </Button>
      </Form>
    </Container>
  );
};

export default GoalsForm;
