import React, { useState, useEffect } from 'react';
import InfoCard from '../InfoCard.js';
import AddMealForm from '../Forms/AddMealForm.js';
import AllMealsPage from '../Pages/AllMealsPage.js';
import InfoCardPieChart from '../Cards/InfoCardPieChart.js';
import '../../css/DashboardPage.css';

const DashboardPage = () => {
    const [userDataAndGoals, setUserDataAndGoals] = useState({
        dailyCalories: 0,
        monthlyCalories: 0,
        currentWeight: 0,
        goalWeight: 0,
        height: 0,
        isDailyCaloriesGoal: true,
    });
    const [calories, setCalories] = useState(0);
    const [errorMessage, setErrorMessage] = useState('');
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchUserDataAndGoals();
        fetchCalories();
    }, []);

    const fetchUserDataAndGoals = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/user/goals`);
            setUserDataAndGoals({
                dailyCalories: data.dailyCaloriesGoal || 0,
                monthlyCalories: data.monthlyCaloriesGoal || 0,
                currentWeight: data.weight || 0,
                goalWeight: data.goalWeight || 0,
                height: data.height || 0,
                isDailyCaloriesGoal: data.isDailyCaloriesGoal,
            });
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchCalories = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/meal/calories?date=${formatDate(new Date())}`);
            setCalories(data);
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchData = async (url) => {
        const response = await fetch(url, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        });

        if (!response.ok) {
            throw new Error('Fetching data failed');
        }

        return response.json();
    };

    const formatDate = (date) => {
        return date.toISOString().split('T')[0];
    };

    const handleAddButtonClick = () => {
        setShowForm(true);
    };

    const handleCloseForm = () => {
        setShowForm(false);
    };

    const {
        dailyCalories,
        monthlyCalories,
        currentWeight,
        goalWeight,
        height,
    } = userDataAndGoals;

    return (
        <div className="dashboard-page">
            {/* New button section for scalability */}
            <div className="button-section">
                <button className="add-button" onClick={handleAddButtonClick}>
                    Add
                </button>
                {/* Future buttons can be added here */}
            </div>

            {showForm && (
                <div className="modal-overlay" onClick={handleCloseForm}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <AddMealForm />
                    </div>
                </div>
            )}

            <div className="upper-section">
                <AllMealsPage />
            </div>
            <div className="main-section">
                <div className="left-section">
                    <InfoCardPieChart
                        title="Calories Consumed Today"
                        current={calories}
                        goal={dailyCalories}
                        unit="kcal"
                    />
                    {/* Future InfoCards can be added here */}
                </div>
                <div className="right-section">
                    <InfoCard
                        title="Current Weight"
                        current={currentWeight}
                        goal={goalWeight}
                        unit="kg"
                    />
                    {/* Future InfoCards can be added here */}
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
