import React, { useState } from "react";
import { Container, Form, Alert, Spinner, Button } from "react-bootstrap";
import "../../css/CustomForm.css";

const AddActivityTypeForm = () => {
  const [activityType, setActivityType] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [options, setOptions] = useState(["Cardio", "Gym", "Solo Sport"]);

  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage("");
    setSuccessMessage("");
    try {
      const response = await fetch(
        "https://localhost:7009/api/admin/add-activity-type",
        {
          method: "POST",
          credentials: "include",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ name: activityType }),
        }
      );
      const data = await response.json();

      if (response.ok) {
        setSuccessMessage(data.message);
      } else {
        setErrorMessage(data.message);
      }
    } catch (err) {
      console.log(err);
      setErrorMessage(err.message);
      setSuccessMessage("");
    } finally {
      setIsLoading(false);
      resetForm();
    }
  };

  const handleChange = (e) => {
    setActivityType(e.target.value);
  };

  const resetForm = () => {
    setActivityType("");
  };

  return (
    <Container className="custom-form-container mt-5 mb-5 p-4">
      {errorMessage && (
        <Alert variant="danger" className="mt-3">
          {errorMessage}
        </Alert>
      )}
      {successMessage && (
        <Alert variant="success" className="mt-3">
          {successMessage}
        </Alert>
      )}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-4">
          <Form.Label className="custom-form-label">
            Activity Type Name
          </Form.Label>
          <Form.Control
            type="text"
            name="Activity Type Name"
            value={activityType}
            onChange={handleChange}
            required={true}
            placeholder="Enter activity type name"
            className="custom-form-control"
          />
        </Form.Group>
        <Form.Group className="mb-4">
          <Form.Label className="custom-form-label">
            Select an Option
          </Form.Label>
          <Form.Select
            name="Selected Option"
            value={selectedOption}
            onChange={handleChange}
            required
            className="custom-form-control"
          >
            <option value="" disabled>
              Choose an option...
            </option>
            {options.map((option) => (
              <option key={option.id} value={option.id}>
                {option.name}
              </option>
            ))}
          </Form.Select>
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          disabled={isLoading}
          className="custom-submit-button"
        >
          {isLoading ? (
            <>
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
                className="me-2"
              />
              Loading...
            </>
          ) : (
            `Add`
          )}
        </Button>
      </Form>
    </Container>
  );
};

export default AddActivityTypeForm;
