import React, { useState, useEffect } from 'react';
import '../../css/GoalsForm.css';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';

const GoalsForm = () => {
  const [goals, setGoals] = useState({
    dailyCalories: '',
    monthlyCalories: '',
    currentWeight: '',
    goalWeight: '',
    height: '',
  });
  
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchGoals = async () => {
      try {
        const response = await fetch('https://localhost:7009/api/user/goals', {
          method: 'GET',
          credentials: 'include',
        });

        if (!response.ok) {
          throw new Error('Retrieving goals failed');
        }

        const data = await response.json();
        setGoals({
          dailyCalories: data.dailyCaloriesGoal || 0,
          monthlyCalories: data.monthlyCaloriesGoal || 0,
          currentWeight: data.weight || 0,
          goalWeight: data.goalWeight || 0,
          height: data.height || 0,
        });
        setErrorMessage('');
      } catch (err) {
        setErrorMessage(err.message);
      }
    };

    fetchGoals();
  }, []);


  const handleChange = (e) => {
    setGoals({ ...goals, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('https://localhost:7009/api/user/change-goals-info', {
        method: 'POST',
        credentials: 'include',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          DailyCaloriesGoal: goals.dailyCalories, 
          MonthlyCaloriesGoal: goals.monthlyCalories,
          Weight: goals.currentWeight,
          GoalWeight: goals.goalWeight,
          Height: goals.height
         })
      });

      if (!response.ok) {
        throw new Error('Changing goals failed');
      }
      setSuccessMessage('Success');
      setErrorMessage('');
    } catch (err) {
      setSuccessMessage('');
      setErrorMessage(err.message);
    }
  };

  return (
    <Container className="goals-form">
      <h2>Your Goals</h2>
      {
        errorMessage != '' 
        ? (errorMessage && (
        <div className="alert alert-danger" role="alert">
          {errorMessage}
        </div>
      )) 
      :
        successMessage && (
          <div className="alert alert-success" role="alert">
            {successMessage}
          </div>
        )
      }
      <Form onSubmit={handleSubmit} className="goals-form">
        <Row>
          <Col md={6}>
            <Form.Group controlId="dailyCalories">
              <Form.Label>Daily Calories Goal</Form.Label>
              <Form.Control
                type="number"
                name="dailyCalories"
                value={goals.dailyCalories}
                onChange={handleChange}
                placeholder="Enter daily calories goal"
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="monthlyCalories">
              <Form.Label>Monthly Calories Goal</Form.Label>
              <Form.Control
                type="number"
                name="monthlyCalories"
                value={goals.monthlyCalories}
                onChange={handleChange}
                placeholder="Enter monthly calories goal"
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col md={6}>
            <Form.Group controlId="currentWeight">
              <Form.Label>Current Weight (kg)</Form.Label>
              <Form.Control
                type="number"
                name="currentWeight"
                value={goals.currentWeight}
                onChange={handleChange}
                placeholder="Enter current weight"
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group controlId="goalWeight">
              <Form.Label>Goal Weight (kg)</Form.Label>
              <Form.Control
                type="number"
                name="goalWeight"
                value={goals.goalWeight}
                onChange={handleChange}
                placeholder="Enter goal weight"
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col md={12}>
            <Form.Group controlId="height">
              <Form.Label>Height (cm)</Form.Label>
              <Form.Control
                type="number"
                name="height"
                value={goals.height}
                onChange={handleChange}
                placeholder="Enter height"
              />
            </Form.Group>
          </Col>
        </Row>
        <Button variant="primary" type="submit" className="fancy-button">
          Save Goals
        </Button>
      </Form>
    </Container>
  );
};

export default GoalsForm;
