            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchCalories = async () => {
        try {
            const data = await fetchData(`https://localhost:7009/api/meal/calories?date=${formatDate(new Date())}`);
            console.log(data);
            setCalories(data);
            setErrorMessage('');
        } catch (err) {
            setErrorMessage(err.message);
        }
    };

    const fetchData = async (url) => {
        const response = await fetch(url, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            }
        });

        if (!response.ok) {
            throw new Error('Fetching data failed');
        }

        return response.json();
    };

    const formatDate = (date) => {
        return date.toISOString().split('T')[0];
    };
  
    return (
      <div className="dashboard-page">
        <h1>Your Fitness Dashboard</h1>
        <div className="dashboard-grid">
          <InfoCard 
            title="Calories Consumed Today" 
            current={calories} 
            goal={userDataAndGoals.dailyCaloriesGoal} 
            unit="kcal" 
          />
          <InfoCard 
            title="Current Weight" 
            current={userDataAndGoals.weight} 
            goal={userDataAndGoals.goalWeight} 
            unit="kg" 
          />
        </div>
      </div>
    );
  };
  
  export default DashboardPage;
