import React, { useState } from 'react';
import '../../css/GeneralForm.css';
import { Form, Button, Container } from 'react-bootstrap';

const GeneralForm = async () => {
  const [email, setEmail] = useState('');

  try {
    const response = await fetch('https://localhost:7009/api/user/profile', {
      method: 'POST',
      credentials: 'include'
    });

    if (!response.ok) {
      throw new Error('Retrieving email failed');
    }
    const data = await response.json();
    setEmail(data)
  } catch (err) {
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit the email change
  };

  return (
    <div className="add-meal-form">
      <h2>Add a New Meal</h2>
      <form onSubmit={handleSubmit} className="meal-form">
        {errorMessage && (
          <div className="alert alert-danger" role="alert">
            {errorMessage}
          </div>
        )}
        <div className="form-group">
          <label htmlFor="mealName">Meal Name</label>
          <input
            type="text"
            id="mealName"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter meal name"
          />
        </div>
        <div className="form-group">
          <label htmlFor="mealType">Meal Type</label>
          <select
            id="mealType"
            value={mealOfTheDay}
            onChange={(e) => setMealOfTheDay(e.target.value)}
          >
            <option value="">Select meal type</option>
            <option value="0">Breakfast</option>
            <option value="1">Lunch</option>
            <option value="2">Dinner</option>
            <option value="3">Snack</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="calories">Calories</label>
          <input
            type="number"
            id="calories"
            value={calories}
            onChange={(e) => setCalories(e.target.value)}
            placeholder="Enter calories"
          />
        </div>
        <button type="submit" className="fancy-button">
          Add Meal
        </button>
      </form>
    </div>
  );
};

export default GeneralForm;
