import React from 'react';
import { motion } from 'framer-motion';
import { FaWeight, FaHeartbeat, FaFire, FaChevronDown } from 'react-icons/fa';
import '../../css/HomePage.css';

const Home = () => {
  return (
    <div className="home-container">
      <header className="header">
        <motion.h1 
          className="title"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
        >
          Welcome to FitLife
        </motion.h1>
        <motion.p 
          className="subtitle"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          Your journey to a healthier life starts here
        </motion.p>
        <motion.div 
          className="chevron"
          initial={{ y: 0 }}
          animate={{ y: 20 }}
          transition={{ repeat: Infinity, duration: 1.5, ease: "easeInOut" }}
        >
          <FaChevronDown size={32} />
        </motion.div>
      </header>

      <section className="features">
        <motion.div 
          className="feature-card"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >
          <FaWeight size={50} className="icon"/>
          <h3>Set Your Goals</h3>
          <p>Define your daily calorie intake, weight goals, and more to stay on track.</p>
        </motion.div>

        <motion.div 
          className="feature-card"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >
          <FaHeartbeat size={50} className="icon"/>
          <h3>Track Your Progress</h3>
          <p>Monitor your weight, height, and other metrics to see your improvements.</p>
        </motion.div>

        <motion.div 
          className="feature-card"
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
        >
          <FaFire size={50} className="icon"/>
          <h3>Stay Motivated</h3>
          <p>Get personalized tips and feedback to keep pushing towards your goals.</p>
        </motion.div>
      </section>

      <footer className="footer">
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1.5 }}
        >
          Â© 2024 FitLife. All Rights Reserved.
        </motion.p>
      </footer>
    </div>
  );
};

export default Home;
