import React from 'react';
import { Canvas } from '@react-three/fiber';
import { Sphere, MeshDistortMaterial, OrbitControls } from '@react-three/drei';
import { motion } from 'framer-motion';
import styled from 'styled-components';

const Home = () => {
  return (
    <Container>
      <Content>
        <TextContainer>
          <motion.h1
            initial={{ y: -250 }}
            animate={{ y: -10 }}
            transition={{ delay: 0.2, type: 'spring', stiffness: 120 }}
          >
            Welcome to FitLife
          </motion.h1>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.4, duration: 1.5 }}
          >
            Set your fitness goals, track your progress, and transform your life with our all-in-one fitness web app.
          </motion.p>
          <motion.button
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
          >
            Get Started
          </motion.button>
        </TextContainer>

        <CanvasContainer>
          <Canvas>
            <OrbitControls enableZoom={false} />
            <ambientLight intensity={0.5} />
            <directionalLight position={[2, 5, 2]} />
            <Sphere args={[1, 100, 200]} scale={2.4}>
              <MeshDistortMaterial color="#8352fd" distort={0.5} speed={2} />
            </Sphere>
          </Canvas>
        </CanvasContainer>
      </Content>

      <Background />
    </Container>
  );
};

export default Home;

// Styled Components

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  overflow: hidden;
`;

const Content = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 80%;
  height: 80%;
`;

const TextContainer = styled.div`
  flex: 1;
  color: white;
  z-index: 1;
  
  h1 {
    font-size: 4rem;
    margin-bottom: 20px;
  }

  p {
    font-size: 1.5rem;
    margin-bottom: 30px;
    line-height: 1.5;
  }

  button {
    font-size: 1.2rem;
    padding: 10px 20px;
    color: white;
    background-color: #ff4d4d;
    border: none;
    border-radius: 25px;
    cursor: pointer;
    box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.2);
    transition: all 0.3s ease;
  }
`;

const CanvasContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const Background = styled.div`
  position: absolute;
  top: -20%;
  left: -20%;
  width: 140%;
  height: 140%;
  background: radial-gradient(circle at center, #ff4d4d 0%, transparent 70%);
  z-index: 0;
  animation: rotateBackground 15s linear infinite;

  @keyframes rotateBackground {
    0% {
      transform: rotate(0deg);
    }
    100% {
      transform: rotate(360deg);
    }
  }
`;
