import React, { useState } from 'react';
import '../../css/AddConsumableForm.css';

const AddConsumableForm = () => {
    const [itemName, setItemName] = useState("");
    const [calories, setCalories] = useState(0);
    const [protein, setProtein] = useState(0.0);
    const [carbohydrates, setCarbohydrates] = useState(0.0);
    const [fat, setFat] = useState(0.0);
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
  
    const [nutritionalInfo, setNutritionalInfo] = useState({
      Carbohydrates: {
        Fibers: 0.0,
        Starch: 0.0,
        Sugars: 0.0,
        galactose: 0.0,
        Glucose: 0.0,
        Sucrose: 0.0,
        lactose: 0.0,
        Maltose: 0.0,
        Fructose: 0.0,
      },
      Vitamins: {
        Betaine: 0.0,
        "Vitamin A": 0.0,
        "Vitamin B1 (thiamine)": 0.0,
        "Vitamin B2 (riboflavin)": 0.0,
        "Vitamin B3 (niacin)": 0.0,
        "Vitamin B4 (choline)": 0.0,
        "Vitamin B5 (Pantothenic Acid)": 0.0,
        "Vitamin B6 (Pyridoxine)": 0.0,
        "Vitamin B9 (Folic Acid)": 0.0,
        "Vitamin B12 (Cobalcamine)": 0.0,
        "Vitamin C": 0.0,
        "Vitamin D": 0.0,
        "Vitamin E": 0.0,
        "Vitamin K1": 0.0,
        "Vitamin K2 (MK04)": 0.0,
      },
      AminoAcids: {
        Alanine: 0.0,
        Arginine: 0.0,
        "Aspartic acid": 0.0,
        Valin: 0.0,
        Glycine: 0.0,
        Glutamine: 0.0,
        Isoleucine: 0.0,
        Leucine: 0.0,
        Lysine: 0.0,
        Methionine: 0.0,
        Proline: 0.0,
        Serine: 0.0,
        Tyrosine: 0.0,
        Threonine: 0.0,
        tryptophan: 0.0,
        Phenylalanine: 0.0,
        Hydroxyproline: 0.0,
        histidine: 0.0,
        Cystine: 0.0,
      },
      Fats: {
        "Monounsaturated fats": 0.0,
        "Polyunsaturated fats": 0.0,
        "Saturated fat": 0.0,
        "Trans fats": 0.0,
      },
      Minerals: {
        Iron: 0.0,
        Potassium: 0.0,
        Calcium: 0.0,
        Magnesium: 0.0,
        Manganese: 0.0,
        Med: 0.0,
        Sodium: 0.0,
        Selenium: 0.0,
        Fluoride: 0.0,
        Phosphorus: 0.0,
        Zinc: 0.0,
      },
      Sterols: {
        Cholesterol: 0.0,
        Phytosterols: 0.0,
        Stigmasteroli: 0.0,
        Campesteroli: 0.0,
        "Beta-sitosterols": 0.0,
      },
      More: {
        Alcohol: 0.0,
        Water: 0.0,
        Caffeine: 0.0,
        Theobromine: 0.0,
        Ashes: 0.0,
      },
    });
  
    const handleSubmit = async (e) => {
      e.preventDefault();
  
      const nutritionalInformationArray = Object.entries(nutritionalInfo).flatMap(
        ([category, nutrients]) =>
          Object.entries(nutrients).map(([name, amount]) => ({
            category,
            name,
            amount,
          }))
      );
    
      const consumableItem = {
        Name: itemName,
        CaloriesPer100g: calories,
        ProteinPer100g: protein,
        CarbohydratePer100g: carbohydrates,
        FatPer100g: fat,
        NutritionalInformation: nutritionalInformationArray,
      };
  

        try {
            const response = await fetch('https://localhost:7009/api/admin/add-consumable-item', {
                method: 'POST',
                credentials: 'include',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(consumableItem),
            });

            if (!response.ok) {
                throw new Error('Adding a consumable item failed');
            }

            setSuccessMessage('Consumable item added successfully!');
        } catch (err) {
            setErrorMessage(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const renderNutritionalSection = (sectionName, sectionData, title) => (
        <div className={`collapsible-card ${sections[sectionName] ? 'expanded' : ''}`}>
            <div className="collapsible-header" onClick={() => toggleSection(sectionName)}>
                <h3>{title}</h3>
            </div>
            {sections[sectionName] && (
                <div className="collapsible-content grid-container">
                    {Object.entries(sectionData).map(([key, value]) => (
                        <div className="form-group grid-item" key={key}>
                            <label>{key.charAt(0).toUpperCase() + key.slice(1)}</label>
                            <input
                                type="number"
                                step="0.01"
                                name={key}
                                value={value}
                                onChange={(e) => handleNutritionalChange(sectionName, key, e.target.value)}
                            />
                        </div>
                    ))}
                </div>
            )}
        </div>
    );

    return (
        <div className="add-consumable-form">
            <h2>Add New Consumable</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Type:</label>
                    <select name="type" value={formData.type} onChange={handleChange} required>
                        <option value="">Select Type</option>
                        <option value="Food">Food</option>
                        <option value="Drink">Drink</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Calories per 100g:</label>
                    <input type="number" name="caloriesPer100g" value={formData.caloriesPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Protein per 100g:</label>
                    <input type="number" step="0.01" name="proteinPer100g" value={formData.proteinPer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Carbohydrate per 100g:</label>
                    <input type="number" step="0.01" name="carbohydratePer100g" value={formData.carbohydratePer100g} onChange={handleChange} required />
                </div>

                <div className="form-group">
                    <label>Fat per 100g:</label>
                    <input type="number" step="0.01" name="fatPer100g" value={formData.fatPer100g} onChange={handleChange} required />
                </div>

                {renderNutritionalSection('carbohydrates', nutritionalInfo.carbohydrates, 'Carbohydrate Information')}
                {renderNutritionalSection('vitamins', nutritionalInfo.vitamins, 'Vitamin Information')}
                {renderNutritionalSection('aminoAcids', nutritionalInfo.aminoAcids, 'Amino Acid Information')}
                {renderNutritionalSection('fats', nutritionalInfo.fats, 'Fat Information')}
                {renderNutritionalSection('minerals', nutritionalInfo.minerals, 'Mineral Information')}
                {renderNutritionalSection('moreInfo', nutritionalInfo.moreInfo, 'More Information')}

                <button type="submit" className="submit-button">Add Consumable</button>
            </form>
        </div>
    );
};

export default AddConsumableForm;
