import React, { useState, useEffect } from 'react';
import '../../css/GeneralForm.css';
import { Form, Button, Container } from 'react-bootstrap';

const GeneralForm = () => {
  const [email, setEmail] = useState('');

  useEffect(() => {
    const fetchEmail = async () => {
      try {
        const response = await fetch('https://localhost:7009/api/user/profile', {
          method: 'POST',
          credentials: 'include',
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error('Retrieving email failed');
        }

        const data = await response.json();
        console.log(data)
        setEmail(email);
      } catch (err) {
        console.error('Failed to fetch email:', err);
      }
    };

    fetchEmail();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Submit the email change
  };

  return (
    <Container className="general-form">
      <h2>General Information</h2>
      <Form onSubmit={handleSubmit} className="general-form">
        <Form.Group controlId="email">
          <Form.Label>Email Address</Form.Label>
          <Form.Control
            type="email"
            onChange={(e) => setEmail(e.target.value)}
            placeholder={email}
          />
        </Form.Group>
        <Button variant="primary" type="submit" className="fancy-button">
          Update Email
        </Button>
      </Form>
    </Container>
  );
};

export default GeneralForm;
