import React, { useState } from 'react';
import '../../css/GeneralForm.css';
import { Form, Button, Container } from 'react-bootstrap';

const GeneralForm = async () => {
  const [email, setEmail] = useState('');

  // try {
  //   const response = await fetch('https://localhost:7009/api/user/profile', {
  //     method: 'POST',
  //     credentials: 'include'
  //   });

  //   if (!response.ok) {
  //     throw new Error('Retrieving email failed');
  //   }
  //   const data = await response.json();
  //   setEmail(data)
  // } catch (err) {
  // }

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Submit the email change
  };

  <div className="general-form">
  <h2>General Information</h2>
  <form onSubmit={handleSubmit} className="form-container">
    <div className="form-group">
      <label htmlFor="email">Email Address</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Enter your email"
        required
      />
    </div>
    <button type="submit" className="fancy-button">
      Update Email
    </button>
  </form>
</div>
};

export default GeneralForm;
